import { Carousel , Image} from 'antd'
import React from 'react'
import './index.css'
import h1 from '../../assets/images/house01.jpg'
import h2 from '../../assets/images/house02.jpg'
import h3 from '../../assets/images/house03.jpg'
import h4 from '../../assets/images/house04.jpg'



export default function index() {
    
    const myimages=[
        {
            src:h1,
            text:'无与伦比的服务'
        },
        {
            src:h2,
            text:'外出时有家的感觉'
        },
        {
            src:h3,
            text:'卓越品质，独特体验'
        },
        {
            src:h4,
            text:'个性化舒适'
        }
    ]
    function MyImage(props){

        const imgstyle={
            backgroundImage:`url(${props.src})`,
            
        }
        const textstyle={
            // position:'absolute',
            // top:'0',
            // textAlign:'center',
           
        }
        return(
            <div className='carouselStyle' >
              <Image 
                    width="100%"
                    height="100%"
                    src={props.src}
                    preview={false}
                />
                
               <div className='carouselText' style={textstyle}> 
                    <div className='changingText'>
                        <h1><span className='animation1'>开始您的</span><br/><span className='animation2'>下一次远足探险</span></h1>
                        <p > {props.text}</p>
                    </div>
                    <span className='button'>了解更多</span>
                </div>
            </div>
        )
    }


    return (
        <div>
            <Carousel autoplay  autoplaySpeed={6000} >
               { 
                myimages.map(item=>{
                    return <MyImage src={item.src} text={item.text} />
                })
               }
               
            </Carousel>

        </div>
    )
}
